mapper.select:

SELECT l.lecture_name, pn.title, pn.submitdate, pn.notice_no, header
FROM lecture l
JOIN professor_notice pn on l.lecture_no = pn.lecture_no and l.ta_no = ${ta_no}


UNION ALL

SELECT l.lecture_name, lf.file_name, lf.file_time, lf.no, header
FROM lecture l
JOIN lecturefile lf on l.lecture_no = lf.lecture_no and l.ta_no = ${ta_no}

ORDER BY submitdate DESC 
LIMIT #{start}, #{pagination.sz}

SELECT COUNT(*) FROM( SELECT l.lecture_name, pn.title, pn.submitdate, pn.notice_no, header FROM lecture l JOIN professor_notice pn on l.lecture_no = pn.lecture_no and l.ta_no = ${ta_no}
UNION ALL
SELECT l.lecture_name, lf.file_name, lf.file_time, lf.no, header FROM lecture l JOIN lecturefile lf on l.lecture_no = lf.lecture_no and l.ta_no = ${ta_no}
ORDER BY submitdate DESC ) AS tem
